import React from 'react';

const Speedometer = ({ value }) => {
  // Calculate the width of the progress bar based on the value
  const progressWidth = `${(value / 10000) * 100}%`;

  return (
    <div className="relative w-32 h-32">
      <div className="absolute top-0 left-0 right-0 text-center">
        <span className="text-xs text-gray-600">Users</span>
      </div>
      <div className="flex items-center justify-center h-full">
        <div className="relative w-full h-full">
          <div
            className="absolute top-0 left-0 right-0 bottom-0 rounded-full bg-gray-300"
          ></div>
          <div
            className="absolute top-0 left-0 right-0 bottom-0 rounded-full bg-green-500"
            style={{
              clip: `rect(0, 16px, 32px, 0)`, // Adjust clip to make the progress circular
              width: '100%',
              transform: `rotate(${progressWidth / 100 * 180}deg)`, // Rotate the progress bar
              transformOrigin: 'center',
            }}
          ></div>
          <div className="absolute top-0 left-0 right-0 bottom-0 flex items-center justify-center">
            <span className="text-lg font-bold text-gray-700">{value}+ users</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const userCount = 5500; // Example user count

  return (
    <div className="App p-4">
      <h1 className="text-2xl font-bold mb-4">User Speedometer</h1>
      <Speedometer value={userCount} />
    </div>
  );
};

export default App;
