import { useState } from 'react';
import { FaAngleDown, FaAngleUp } from 'react-icons/fa';

const Lists = () => {
  const [showContent, setShowContent] = useState([false, false, false, false, false]);

  const toggleContent = (index) => {
    const newShowContent = [...showContent];
    newShowContent[index] = !newShowContent[index];
    setShowContent(newShowContent);
  };

  return (
    <div>
      <h1 className='font-bold text-center text-black text-2xl p-6'>
        Online coaching lessons for remote learning.
      </h1>
      <div>
        {[0, 1, 2, 3, 4].map((index) => (
          <div className="mb-10" key={index}>
            <div className="custom-heading flex justify-between items-center relative">
              <h1 className="flex items-center mb-4">
                <span className="w-4 h-4 bg-blue-500 rounded-full mr-2"></span>
                <span>Title {index}</span>
              </h1>
              <button onClick={() => toggleContent(index)} className="text-blue-500">
                {showContent[index] ? <FaAngleUp /> : <FaAngleDown />}
              </button>
              {!showContent[index] && <hr className="w-full h-0.5 bg-blue-500 absolute bottom-0 left-0" />}
            </div>
            {showContent[index] && (
              <div>
                <p className="mb-4">
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus
                  aliquet, felis in congue vehicula, consectetur adipiscing elit.
                </p>
                <hr className="w-full h-0.5 bg-blue-500 mb-4" />
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Lists;
