import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock, faMoneyBillAlt, faCheckCircle, faTimesCircle } from '@fortawesome/free-solid-svg-icons';
import Lottie from 'lottie-react';
import cardbg from '../assets/cardbg.json';

const CatCard = () => {
  const [selectedCards, setSelectedCards] = useState([]);
  const [gameConfirmed, setGameConfirmed] = useState(false);

  const handleClick = (index) => {
    if (!gameConfirmed) {
      setSelectedCards((prevSelected) => {
        if (prevSelected.includes(index)) {
          // If the card is already selected, remove it from the selectedCards array
          return prevSelected.filter((item) => item !== index);
        } else {
          // If the card is not selected, add it to the selectedCards array
          return [...prevSelected, index];
        }
      });
    }
  };

  const handleConfirm = () => {
    setGameConfirmed(true);
  };

  const isCardSelected = (index) => {
    return selectedCards.includes(index);
  };

  return (
    <div className='bg-[#020917]'>
      <h2 className="text-3xl text-white font-bold text-center">Category one</h2>

      <div className="flex w-screen h-78vh ">
        <div className="rounded-lg shadow-lg p-6 ">
          <p className="text-gray-600 text-lg text-center p-5">Choose your money cards below.</p>
          <div className="grid grid-cols-3 gap-6 mx-16 bg-white p-10">
            {[...Array(10)].map((_, index) => (
              <div key={index}
                   className={`rounded-lg shadow p-4 flex flex-col items-center justify-center border-2 border-orange-500 transition duration-300 ease-in-out cursor-pointer 
                   ${isCardSelected(index) ? 'bg-white text-black' : 'bg-[#020917] text-orange-500'}
                   ${!isCardSelected(index) ? 'hover:bg-blue-800' : ''}`}
                   onClick={() => handleClick(index)}>
                <div className="text-4xl font-bold mb-2 flex items-center justify-center" style={{ width: '50px', height: '50px' }}>
                  {index + 1}
                </div>
                {gameConfirmed && <FontAwesomeIcon icon={faLock} className="text-red-500 mt-2" />}
              </div>
            ))}
          </div>
          {!gameConfirmed && (
            <button className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4 hover:bg-blue-600" onClick={handleConfirm}>
              Confirm Selection
            </button>
          )}
        </div>

        <div className="flex flex-col justify-center items-center text-white text-center mt-4 h-full">
          <div className="max-w-md">
            <p className="text-lg">
              If the cards are chosen by others, they will be locked.
              <br />
              Each card costs 5 birr. If you win, you get 45 birr added to your balance.
              <br />
              <FontAwesomeIcon icon={faMoneyBillAlt} className="text-yellow-500" /> Happy gaming!
              {!gameConfirmed && <FontAwesomeIcon icon={faTimesCircle} className="text-red-500 ml-2" />}
              {gameConfirmed && <FontAwesomeIcon icon={faCheckCircle} className="text-green-500 ml-2" />}
            </p>
          </div>
        </div>

      </div>
    </div>
  );
};

export default CatCard;
