import React, { useState, useEffect } from 'react';

const Speedometer = ({ value }) => {
  // State to hold the current count for animation
  const [count, setCount] = useState(0);

  // Effect to update the count rapidly until it reaches the actual value
  useEffect(() => {
    const interval = setInterval(() => {
      setCount((prevCount) => {
        const nextCount = prevCount + 1;
        return nextCount > value ? value : nextCount;
      });
    }, 1);

    return () => clearInterval(interval);
  }, [value]);

  // Calculate the fill percentage of the circle
  const fillPercentage = (count / 5500) * 100;

  // Calculate the stroke-dasharray for the circular border
  const strokeDashArray = Math.ceil(2 * Math.PI * 15);

  return (
    <div className="relative w-32 h-32">
      <div className="absolute top-0 left-0 right-0 text-center">
        <span className="text-xs text-gray-600">Users</span>
      </div>
      <div className="flex items-center justify-center h-full">
        <div className="relative w-full h-full">
          <svg
            className="absolute top-0 left-0 right-0 bottom-0 rounded-full"
            width="100%"
            height="100%"
            viewBox="0 0 32 32"
          >
            <circle
              cx="16"
              cy="16"
              r="15"
              fill="none"
              stroke="#10B981"
              strokeWidth="2"
              strokeLinecap="round"
              strokeDasharray={strokeDashArray}
              strokeDashoffset={strokeDashArray - (fillPercentage / 100) * strokeDashArray}
            />
            <circle
              cx="16"
              cy="16"
              r="15"
              fill="none"
              stroke="#E5E7EB"
              strokeWidth="2"
              strokeLinecap="round"
            />
          </svg>
          <div className="absolute top-0 left-0 right-0 bottom-0 flex items-center justify-center">
            <span className="text-lg font-bold text-gray-700">{count} users</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const userCount = 5500; // Example user count

  return (
    <div className="App p-4">
      <h1 className="text-2xl font-bold mb-4">User Speedometer</h1>
      <Speedometer value={userCount} />
    </div>
  );
};

export default App;
