import React, { useState } from 'react';
import Lottie from 'lottie-react';
import cardbg from '../assets/cardbg.json';

const CatCard = () => {
  const [selectedCards, setSelectedCards] = useState([]);

  const handleClick = (index) => {
    setSelectedCards((prevSelected) => {
      if (prevSelected.includes(index)) {
        // If the card is already selected, remove it from the selectedCards array
        return prevSelected.filter((item) => item !== index);
      } else {
        // If the card is not selected, add it to the selectedCards array
        return [...prevSelected, index];
      }
    });
  };

  const isCardSelected = (index) => {
    return selectedCards.includes(index);
  };

  return (
    <div className='bg-[#020917]'>
        <h2 className="text-3xl text-white font-bold text-center">Category one</h2>
                    <p className="text-gray-600 text-lg text-center p-5">choose your cards below.</p>
        
      <div className="flex w-screen h-78vh "> {/* Background color for the container */}
      
        <div className="rounded-lg shadow-lg p-6 ">
         
          <div className="grid grid-cols-3 gap-6 mx-16 bg-white p-10"> {/* Adjusted margin to make cards width shorter */}
            
            {[...Array(10)].map((_, index) => (
              <div key={index} 
                   className={`rounded-lg shadow p-4 flex flex-col items-center justify-center border-2 border-orange-500 transition duration-300 ease-in-out cursor-pointer 
                   ${isCardSelected(index) ? 'bg-white text-black' : 'bg-[#020917] text-orange-500'}
                   ${!isCardSelected(index) ? 'hover:bg-blue-800' : ''}`} // Added conditional hover effect
                   onClick={() => handleClick(index)}> {/* Decreased padding, changed background color, and added hover effect */}
                {/* Decorative number */}
                <div className="text-4xl font-bold mb-2 flex items-center justify-center" style={{ width: '50px', height: '50px' }}> {/* Decreased width and height */}
                  {index + 1}
                </div>
                {/* Content for each section */}
              </div>
            ))}
          </div>
        </div>

        <div className="text-white text-center mt-4">
          Balance = 200 birr
        </div>
      </div>
    </div>
  );
};

export default CatCard;
