import React, { useState } from "react";
import { FaBars, FaSearch, FaUserCircle, FaBell } from "react-icons/fa";
import Notification from "../com/Notification";

const Navbar = ({ sidebarToggle, setSidebarToggle }) => {
  const [showNotification, setShowNotification] = useState(false);

  // Function to toggle the visibility of the Notification pop-up
  const toggleNotification = () => {
    setShowNotification(!showNotification);
  };

  return (
    <nav className="bg-gray-800 px-4 py-3 flex justify-between ">
      <div className="flex items-center text-xl">
        <FaBars
          className="text-white me-4 cursor-pointer"
          onClick={() => setSidebarToggle(!sidebarToggle)}
        />
        <span className="text-white font-semibold">one game</span>
      </div>
      <div className="flex items-center gap-x-5">
        <div className="relative md:w-65">
          <span className="relative md:absolute inset-y-0 left-0 flex items-center pl-2">
            <button className="p-1 focus:outline-none text-white md:text-black">
              <FaSearch />
            </button>
          </span>
          <input
            type="text"
            className="w-full px-4 py-1 pl-12 rounded shadow outline-none hidden md:block"
          />
        </div>

        <div className="text-white" onClick={toggleNotification}>
          <FaBell />
        </div>

        {/* Render Notification component based on showNotification state */}
        {showNotification && <Notification />}

        <div className="relative">
          <button className="text-white group ">
            <FaUserCircle className="w-6 h-6 mt-1" />
            <div className="z-10 hidden absolute bg-white rounded-lg shadow w-32 group-focus:block top-full right-0">
              <ul className="py-2 text-sm text-gray-950">
                <li>
                  <a href="">Profile</a>
                </li>
                <li>
                  <a href="">setting</a>
                </li>
                <li>
                  <a href="">Log out</a>
                </li>
              </ul>
            </div>
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
